mapper.select:
select e.emp_id, e.employee_name, e.employee_number, e.emp_deptid, e.work_type, e.project_type, e.project_description, e.goal, e.completion_ratio, e.completion_result, e.completion_date, e.work_duration, e.extension_field1, e.extension_field2, e.extension_field3, e.create_by, e.create_time, e.update_by, e.update_time, e.remark
from emp_perf e left join sys_dept d on e.emp_deptid = d.dept_id

     and e.employee_name like concat('%', #{employeeName}, '%')
     and e.employee_Number = #{employeeNumber}

        AND (e.emp_deptid = #{empDeptid} OR e.emp_deptid IN ( SELECT t.dept_id FROM sys_dept t WHERE find_in_set(#{empDeptid}, ancestors) ))

     and e.work_type = #{workType}
     and e.project_type = #{projectType}
     and e.completion_result = #{completionResult}
     and DATE_FORMAT(e.completion_date, '%Y-%m') = #{searchDate}

    ORDER BY work_duration desc

mapper.update:
update emp_perf
employee_name = #{employeeName}, employee_number = #{employeeNumber}, emp_deptid = #{empDeptid}, work_type = #{workType}, project_type = #{projectType}, project_description = #{projectDescription}, goal = #{goal}, completion_ratio = #{completionRatio}, completion_result = #{completionResult}, completion_date = #{completionDate}, work_duration = #{workDuration}, extension_field1 = #{extensionField1}, extension_field2 = #{extensionField2}, extension_field3 = #{extensionField3}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where emp_id = #{empId}
